import React, { useState, useEffect } from "react";
import "./styles.css";

export default function Log() {
  const [winner, setWinner] = useState(""); // Updated state variable name
  const [game, setGame] = useState(""); // New state variable
  const [date, setDate] = useState("");
  const [entries, setEntries] = useState([]);
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    // Fetch existing entries when the component mounts
    fetchEntries();
  }, []);

  const fetchEntries = async () => {
    try {
      const response = await fetch("http://localhost:3003/slideshow");
      const data = await response.json();
      setEntries(data.reverse());
    } catch (error) {
      console.error("Error fetching entries:", error);
    }
  };

  const handleSubmit = async () => {
    try {
      // Send the data to the server
      const response = await fetch("http://localhost:3003/slideshow", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          name: name,
          date: date,
        }),
        mode: 'cors',
      });

      const data = await response.json();
      console.log(data);

      // Update the entries and reset form fields
      setEntries([data, ...entries]);
      setSubmitted(true);
      setWinner("");
      setGame("");
      setDate("");
    } catch (error) {
      console.error("Error submitting data:", error);
    }
  };

  const handleDelete = async (entryId) => {
    try {
      await fetch(`http://localhost:3003/slideshow/${entryId}`, {
        method: "DELETE",
        mode: 'cors',
      });

      // Update the entries after deletion
      const updatedEntries = entries.filter((entry) => entry.id !== entryId);
      setEntries(updatedEntries);
    } catch (error) {
      console.error("Error deleting entry:", error);
    }
  };

  return (
    <div>
      <div className="text">
        <h2>Log Game Night</h2>

        <div>
          <label htmlFor="winner">Winner:</label> {/* Updated label and id */}
          <input
            type="text"
            id="winner"
            value={winner}
            onChange={(e) => setWinner(e.target.value)}
            required
          />
        </div>

        <div>
          <label htmlFor="game">Game:</label> {/* New label and id */}
          <input
            type="text"
            id="game"
            value={game}
            onChange={(e) => setGame(e.target.value)}
            required
          />
        </div>

        <div>
          <label htmlFor="date">Date:</label>
          <input
            type="text"
            id="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>

        <div>
          <button type="button" onClick={handleSubmit}>
            Submit
          </button>
        </div>

        {submitted && entries.length > 0 && (
          <div>
            <h3>Recent Entries:</h3>
            <ul>
              {entries.map((entry) => (
                <li key={entry.id}>
                  <strong>{entry.winner}</strong> - {entry.game} - {entry.date} {/* Updated references */}
                  <button onClick={() => handleDelete(entry.id)}>Delete</button>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}
