import React, {useEffect } from "react";
import "./styles.css";
import ProfilePic from "./imgs/profile_pic.JPG";
import Volleyball from "./imgs/volleyball.jpg";

export default function About() {
    useEffect(() => {
        document.title = "About Me";
      }, []);
    return (
      <div className = 'AboutMe'>
        <div className = 'text'>
        <h2>About Me</h2>
          <img src={ProfilePic} alt="Resume" className = 'float-right'/>

          <p>
            I was born and raised in Cupertino, CA. Growing up in the Bay, I've always known that I wanted to do a STEM related profession. Since my dad
            is a software engineer, I naturally gravitated towards that.
          </p>
          <p>
            Outside of academics I spend a lot of his time being active. I've been playing volleyball since I was 12 years old and have 
            played on USC Men's Club Volleyball team for my entire duration at USC. Beyond volleyball, I also enjoys working out, hiking, 
            and skiing/snowboarding.
          </p>

          <h2>Academic Background</h2>

          <p>
            I'm currently a senior at University of Southern California pursuing
            a double major in Data Science and Economics. Throughout my coursework for my Data Science degree, I took various 
            front-end development courses such as Full-Stack Web Development, Advanced Front-end Web Development, Design for UI/UX,
            and Design for Data Visualization. These courses paired with the various database courses fostered a passion for full-stack
            web development. 
          </p>

          <img src={Volleyball} alt="volleyball" className = 'float-left'/>
        </div>


      </div>
    );
  }
