import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom"; // Import Link from react-router-dom
import "./styles.css";

export default function Log() {
  const [winner, setWinner] = useState(""); 
  const [game, setGame] = useState(""); 
  const [date, setDate] = useState("");
  const [entries, setEntries] = useState([]);
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    // fetch existing entries when the component mounts
    fetchEntries();
    console.log("Fetching existing entries")
  }, []);

  const fetchEntries = async () => {
    try {
      const response = await fetch("http://localhost:3003/logWinner");
      const data = await response.json();
      setEntries(data.reverse());
    } catch (error) {
      console.error("Error fetching entries:", error);
    }
  };

  const handleSubmit = async () => {
    try {
      // Send the data to the server
      const response = await fetch("http://localhost:3003/logWinner", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          winner: winner,
          game: game,
          date: date,
        }),
        mode: 'cors',
      });

      const data = await response.json();
      console.log(data);

      // Update the entries and reset form fields
      setEntries([data, ...entries]);
      setSubmitted(true);
      setWinner("");
      setGame("");
      setDate("");
    } catch (error) {
      console.error("Error submitting data:", error);
    }
  };

  const handleDelete = async (entryId) => {
    try {
      await fetch(`http://localhost:3003/logWinner/${entryId}`, {
        method: "DELETE",
        mode: 'cors',
      });

      // update the entries after deletion
      const updatedEntries = entries.filter((entry) => entry.id !== entryId);
      setEntries(updatedEntries);
    } catch (error) {
      console.error("Error deleting entry:", error);
    }
  };

  return (
    <div>
      <div className="text">
        <h2>Log Game Night</h2>

        <div>
          <label htmlFor="winner">Winner:</label> 
          <input
            type="text"
            id="winner"
            value={winner}
            onChange={(e) => setWinner(e.target.value)}
            required
          />
        </div>

        <div>
          <label htmlFor="game">Game:</label> 
          <input
            type="text"
            id="game"
            value={game}
            onChange={(e) => setGame(e.target.value)}
            required
          />
        </div>

        <div>
          <label htmlFor="date">Date:</label>
          <input
            type="text"
            id="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>

        <div>
          <button type="button" onClick={handleSubmit}>
            Submit
          </button>
        </div>

        {entries.length > 0 && (
          <div>
            <h2>Recent Entries:</h2>
            <ul>
              {entries.map((entry) => (
                <li key={entry.id}>
                  <strong>{entry.winner}</strong> - {entry.game} - {entry.date} 
                  <button onClick={() => handleDelete(entry.id)}>Delete</button>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>

      <div>
          <Link to="/statistics">
            <button>See Statistics</button>
          </Link>
        </div>
    </div>
  );
}
