import React, {useEffect } from "react";
import "./styles.css";
import ProfilePic from "./imgs/profile_pic.JPG";
import Volleyball from "./imgs/volleyball.jpg";



export default function About() {
    useEffect(() => {
        document.title = "About Me";
      }, []);
    return (
      <div className = 'AboutMe'>
        <div className = 'text'>
        <h2>About Me</h2>
          <img src={ProfilePic} alt="Resume" className = 'float-right'/>

          <p>
            Charlie was born and raised in Cupertino, CA. He is currently a senior at University of Southern California pursuing
            a double major in Data Science and Economics. Throughout his coursework for his Data Science degree, he took various 
            front-end development courses such as Full-Stack Web Development, Advanced Front-end Web Development, Design for UI/UX,
            and Design for Data Visualization. These courses paired with the various database courses fostered a passion for full-stack
            web development. 

          </p>

          <p>
            Outside of academics he spends a lot of his time being active. He's been playing volleyball since he was 12 years old and has 
            played on USC Men's Club Volleyball team for his entire duration at USC. Beyond volleyball, he also enjoys working out, hiking, and skiing/snowboarding.
          </p>

          <img src={Volleyball} alt="volleyball" className = 'float-left'/>
        </div>


      </div>
    );
  }
