import React, { useState, useEffect } from "react";
import "./styles.css";

export default function Statistics() {
    const [entries, setEntries] = useState([]);
    const [winnerPercentages, setWinnerPercentages] = useState([]);

    useEffect(() => {
        document.title = "Statistics";
    }, []);

    useEffect(() => {
        // fetch existing entries when the component mounts
        fetchEntries();
        console.log("Fetching existing entries in statistics")
    }, []);

    useEffect(() => {
        // recalculate percentages whenever entries change in the log changes page
        calculateWinnerPercentages();
        console.log("Updating winner percentages");
      }, [entries]);

    const fetchEntries = async () => {
    try {
          const response = await fetch("http://localhost:3003/logWinner");
          const data = await response.json();
          setEntries(data.reverse());
        } catch (error) {
          console.error("Error fetching entries:", error);
        }
    };


    return (
        <div>
            <div className="text">
                <h2>Statistics</h2>
                {entries.length > 0 && (
                <div>
                    <h2>Winners:</h2>
                    <ul>
                    {entries.map((entry) => (
                        <li key={entry.id}>
                        {entry.winner} - {entry.game} - {entry.date} 
                        </li>
                    ))}
                    </ul>
                </div>
                )}
            </div>
        </div>
  );
}
